import { makeStyles } from '@material-ui/core/styles';

// Callback function
export default makeStyles(() => ({
    root: {
        '& .MuiTextField-root': {
            // margin: theme.spacing(1),
            margin: spacing(1),
        },
    },
    paper: {
        // padding: theme.spacing(2),
        padding: spacing(2),
    },
    form: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
    },
    fileInput: {
        width: '97%',
        margin: '10px 0',
    },
    buttonSubmit: {
        marginBottom: 10,
    },
}));
